            }
            else if (num >= 75)
            {
                Add("risk_model_high");
            }
            else if (num >= 50)
            {
                Add("risk_model_medium");
            }
            else if (num >= 25)
            {
                Add("risk_model_low");
            }
            Add("'>");
            Add(num);
            Add(@"</td>");
        }

